import React, { useState, useEffect } from 'react';
import axios from 'axios';
import employeeService from '../services/employee.service';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';

function ParentDataDisplay() {
  const [parentData, setParentData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const handleClick = (x)=>{

    const selectedParent = parentData.find(parent => parent.parentId === x);
    // Now `selectedParent` holds the parent object for the clicked parent ID
    console.log("Selected Parent:", selectedParent);

  }

  const fetchData = () => {
    employeeService
      .viewAllParent()
      .then((response) => {
        console.log(response.data);
        setParentData(response.data);
      })
      .catch((error) => {
        console.log('Something went wrong', error);
      });
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Parent Data</h1>
      <div className="row">
        <div className="col">
          <div className="card">
            <div className="card-body">
              <div className=" row table-header">
                <div className="col">Parent ID</div>
                <div className="col">Email</div>
                <div className="col">Password</div>
                <div className="col">Date of Birth</div>
                <div className="col">Gender</div>
                <div className="col">Address</div>
                <div className="col">Mobile No</div>
                <div className="col">First Name</div>
                <div className="col">Last Name</div>
                <div className="col"></div>
                <div className="col"></div>
              </div>
              {parentData.map((parent) => (
                <div key={parent.parentId} className="row table-row">
                  <div className="col">{parent.parentId}</div>
                  <div className="col">{parent.email}</div>
                  <div className="col">{parent.password}</div>
                  <div className="col">{parent.dob}</div>
                  <div className="col">{parent.gender}</div>
                  <div className="col">{parent.address}</div>
                  <div className="col">{parent.mobileNo}</div>
                  <div className="col">{parent.pfirstName}</div>
                  <div className="col">{parent.plastName}</div>
                  {/* <div className="col"><a className='btn btn-warning' href=''>Update</a></div> */}

                <button className='btn btn-warning' onClick={()=> handleClick(parent.parentId)}>Update</button>




                  <div className="col"><a className='btn btn-danger' href=''>Delete</a></div>

                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ParentDataDisplay;
